#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Mon Mar 1@author: graceday"""import argparseparser = argparse.ArgumentParser(    description="Generate LAMMPS input files for performing dynamics "                "at 10000K at different densities."    )parser.add_argument(    "--factor",    type=float,    default=1.0,    help="Factor by which to scale in z direction (x, y remain unchanged)."    )parser.add_argument(    "--pot",    type=str,    default="tersoff",    help="tersoff or lcbop."    )parser.add_argument(    "--step",    type=int,    default="10000",    help="step no"    )args = parser.parse_args()# DIRNAME cool_{factor:.2f}_{pair_style}_{step}def write_scaled_input():    with open(f"./deform_{factor:.2f}_{pair_style}/cool_{factor:.2f}_{pair_style}_{step}/cool_{factor:.2f}_{pair_style}_{step}.inpt", "w") as f:        f.write(f"log log.cool_{factor:.2f}_{pair_style}_{step} \n"                "units metal \n"                "\n"                "atom_style atomic \n"                "dimension 3 \n"                "boundary p p p \n"                "\n"                f"read_restart restart.deform_{factor:.2f}_{pair_style}_{step} \n"                "\n"                "mass 1 12.00 \n"                "\n"                f"pair_style {pair_style} \n"                f"# pair_coeff * * /usr/local/Cellar/lammps/2020-10-29/share/lammps/potentials/{pair_coeff} \n"                f"pair_coeff * * /home/dirac/oxford/newc5436/LAMMPS_potentials/{pair_coeff} \n"                "\n"                "thermo 1000 \n"                "thermo_style custom step atoms temp pe etotal vol press density \n"                "\n"                f"dump 1 all atom 100 cool_{factor:.2f}_{pair_style}_{step}.lammpstrj \n"                "dump_modify 1 scale no \n"                "dump_modify 1 first yes \n"                "\n"                f"fix 1 all nvt temp 300 300 1 \n"                "run 10000 \n"                "unfix 1 \n"                "\n"                "undump 1 \n"                "variable density equal density \n"                f"print ${{density}} file ./cool_{factor:.2f}_{pair_style}_{step}_density.dat \n"                "variable total_energy_per_atom equal etotal/atoms \n"                "variable volume_per_atom equal vol/atoms \n"                f"print ${{total_energy_per_atom}} file ./cool_{factor:.2f}_{pair_style}_{step}_energy.dat \n"                f"print ${{volume_per_atom}} file ./cool_{factor:.2f}_{pair_style}_{step}_volume.dat \n"                f"write_data cool_{factor:.2f}_{pair_style}_{step}.data \n"                f"write_restart restart.cool_{factor:.2f}_{pair_style}_{step} \n"                )def main():    write_scaled_input()    print(f"Successfully wrote input file to cool_{factor:.2f}_{pair_style}_{step}.inpt.")if __name__ == "__main__":    step = args.step    factor = args.factor    pair_style = args.pot    if pair_style == "tersoff":        pair_coeff = "SiCGe.tersoff C"    elif pair_style == "lcbop":        pair_coeff = "C.lcbop C"    else:        quit("Invalid potential entered.")    main()